INSERT INTO `xt_pdf_manager_content` (`template_id`, `language_code`, `template_body`)
VALUES
	(1,'de','<!DOCTYPE HTML PUBLIC \"-//W3C//DTD HTML 4.01 Transitional//EN\" \"http://www.w3.org/TR/html4/loose.dtd\">\n<html>\n<head>\n    <meta http-equiv=\"Content-Type\" content=\"text/html; charset=UTF-8\" />\n    <title>{txt key=TEXT_XT_ORDERS_INVOICES}</title>\n    <style type=\"text/css\">\n        {literal}\n        @page { margin: 0px; padding: 0;}\n        body { margin:0; font-family: \"DejaVu Sans\"; font-size: 10px; color: #000000; padding: 15mm 10mm 20mm 25mm;}\n\n        .heading, .dates, .bankdetails, .total_table{ clear: both; display: block; height: auto; min-height: 10px; padding: 0em 0.5em 0.5em 0.5em; width: auto; }\n        .table-item { clear: both; display: block; height: auto; width: auto; }\n        .total{width:70px;text-align:right;}\n        div.data { display: block; margin: 0.5em; }\n        ul { list-style: none inside none; margin: 0; padding: 0;}\n        li { margin: 0; padding: 0; line-height: 1.2em; font-size: 1.1em; }\n        li .name{width:100px;text-align: left;}\n        li .value{width:70px;text-align: right;}\n        li strong{ font-size: 1.2em; line-height: 1.8em;}\n        .left { float: left; }\n        .right-DISABLED { float: right;}\n        .alignr {text-align: right;}\n        table{ width:100%;  padding: 0.5em; border-collapse: collapse;}\n\n        thead, tfoot { font-weight: bold; font-size: 1.2em; line-height: 1.2em; border-bottom: 2px solid #000000; color: #000000; }\n        tr td { border-bottom: 1px solid #434343; line-height: 1.3em; padding-bottom: 0.5em; vertical-align:top}\n        tr.item td { line-height: 1.3em; padding: 0.5em; border: none; }\n        tr.item td.name { padding-left: 0em;font-family: times; }\n        tr.last td { border-bottom: 1px solid #434343; }\n        #footer { width:100%; padding: 0 10mm 0 25mm; position: fixed; bottom: 0; height: 25mm; }\n        #footer tr td { padding: 0; line-height: 1.3em;}\n        #footer tr.last td { border-bottom: none; }\n        table.addresses {width:14cm; margin:0; padding:0;  border-bottom-width: 0px}\n        table.addresses td {vertical-align: top;}\n        table.addresses  tr  thead, table.addresses td { border-bottom-width: 0px}\n        .clear{clear:both;}\n        {/literal}\n    </style>\n</head>\n<body>\n{hook key=invoice_top}\n<!------------------------------------------------- START FOOTER ------------------------------------------------->\n<div id=\"footer\">\n    <table>\n        <tbody>\n        <tr>\n            <td>{$data.shopUrl}</td>\n            <td class=\"alignr\">{txt key=TEXT_XT_ORDERS_INVOICES_ISSUED_WITH} <a href=\"http://www.xt-commerce.com\">xt:Commerce</a></td>\n        </tr>\n        <tr class=\"last\">\n            <td><span style=\"font-weight:bold\">{$data.config._store_shopowner_company}</span>&nbsp; - &nbsp;{$data.config._store_country}-{$data.config._store_shopowner_zip} {$data.config._store_shopowner_city}&nbsp; - &nbsp;{$data.config._store_shopowner_streetaddress}&nbsp; - &nbsp;{txt key=TEXT_XT_ORDERS_INVOICES_VAT_ID}: {$data.config._store_shopowner_vatid}</td>\n            <td></td>\n        </tr>\n        </tbody>\n    </table>\n</div>\n<!------------------------------------------------- END FOOTER ------------------------------------------------->\n<div id=\"wrapper\">\n    <!------------------------------------------------- START HEAD ------------------------------------------------->\n    <div id=\"logo\" style=\"height:20mm;overflow:hidden; \" >\n        <img src=\"{$data.logopath}{$data.logo}\" />\n    </div>\n    <!------------------------------------------------- END HEAD ------------------------------------------------->\n    <!------------------------------------------------- START HEADING ------------------------------------------------->\n\n    <table class=\"addresses\" style=\"margin-top: 5mm; width:100%\"><tr>\n        <td style=\"width:75mm;padding-right:10mm\">\n                <ul>\n                <li>\n                    <div style=\"font-size:1.6mm;margin-bottom:2mm;\"><span style=\"font-weight:bold\">{$data.config._store_shopowner_company}</span>&nbsp; - &nbsp;{$data.config._store_country}-{$data.config._store_shopowner_zip} {$data.config._store_shopowner_city}&nbsp; - &nbsp;{$data.config._store_shopowner_streetaddress}</div></li>\n                    {if !empty($data.order.order_data.billing_company)}\n                    <li>{$data.order.order_data.billing_company}</li>\n                    {/if}\n                    {if !empty($data.order.order_data.billing_company_2)}\n                    <li>{$data.order.order_data.billing_company_2}</li>\n                    {/if}\n                    {if !empty($data.order.order_data.billing_company_3)}\n                    <li>{$data.order.order_data.billing_company_3}</li>\n                    {/if}\n                    <li>{$data.order.order_data.billing_firstname} {$data.order.order_data.billing_lastname}</li>\n                    <li>{$data.order.order_data.billing_street_address}</li>\n                    {if !empty($data.order.order_data.billing_address_addition)}\n                    <li>{$data.order.order_data.billing_address_addition}</li>\n                    {/if}\n                    <li>{$data.order.order_data.billing_postcode} - {$data.order.order_data.billing_city}</li>\n                    <li>{$data.order.order_data.billing_country}</li>\n                </ul>\n            </td>\n        <td style=\"width:35mm;\">\n                    <ul>\n                <li><span style=\"font-weight: bold\">{txt key=TEXT_DELIVERY_ADDRESS}:</span></li>\n                        {if !empty($data.order.order_data.delivery_company)}\n                        <li>{$data.order.order_data.delivery_company}</li>\n                        {/if}\n                        {if !empty($data.order.order_data.delivery_company_2)}\n                        <li>{$data.order.order_data.delivery_company_2}</li>\n                        {/if}\n                        {if !empty($data.order.order_data.delivery_company_3)}\n                        <li>{$data.order.order_data.delivery_company_3}</li>\n                        {/if}\n                        <li>{$data.order.order_data.delivery_firstname} {$data.order.order_data.delivery_lastname}</li>\n                        <li>{$data.order.order_data.delivery_street_address}</li>\n                        {if !empty($data.order.order_data.delivery_address_addition)}\n                        <li>{$data.order.order_data.delivery_address_addition}</li>\n                        {/if}\n                        <li>{$data.order.order_data.delivery_postcode} - {$data.order.order_data.delivery_city}</li>\n                        <li>{$data.order.order_data.delivery_country}</li>\n\n                    </ul>\n        </td>\n        <td class=\"data right alignr\" >\n    <!------------------------------------------------- START DATES ------------------------------------------------->\n            <ul>\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_VAT_ID}: {$data.config._store_shopowner_vatid}</li>\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_DATE}: {$data.invoice.invoice_issued_date|date_format:\"%d/%m/%Y\"}</li>\n                {if $data.invoice.invoice_due_date}<li>{txt key=TEXT_XT_ORDERS_INVOICES_DUE_DATE}: {$data.invoice.invoice_due_date|date_format:\"%d/%m/%Y\"}</li>{/if}\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_ORDERED_DATE}: {$data.invoice.invoice_ordered_date|date_format:\"%d/%m/%Y\"}</li>\n                <li><br />{txt key=TEXT_PAYMENT_ON_INVOICE}: {$data.order.order_data.payment_name}</li>\n                {if $data.order.order_data.transaction_id}<!--li>{txt key=TEXT_TRANSACTION_ID}: {$data.order.order_data.transaction_id}</li-->{/if}\n                <li><br />{txt key=TEXT_XT_ORDERS_INVOICES_CUSTOMERS_ID}: {$data.invoice.customers_id}</li>\n                {if !empty($data.order.order_data.customers_vat_id)}\n                <li>{txt key=TITLE_CUSTOMER} {txt key=TEXT_XT_ORDERS_INVOICES_VAT_ID}: {$data.order.order_data.customers_vat_id}</li>\n                {/if}\n                <li><span style=\"font-weight: bold\">{txt key=TEXT_XT_ORDERS_INVOICES_NUMBER}: {$data.invoice.invoice_prefix}{$data.invoice.invoice_number}</span><br />&nbsp;</li>\n                <li><span style=\"font-weight: bold\">{txt key=TEXT_XT_ORDERS_INVOICES_ORDER_ID}: {$data.invoice.orders_id}</span></li>\n            </ul>\n    <!------------------------------------------------ END HEADING ------------------------------------------------->\n        </td>\n    </tr>\n    </table>\n\n    <!------------------------------------------------- START ITEM LIST ------------------------------------------------->\n    {if $data.invoice.invoice_subtotal_discount>0}\n    {assign var=\"showDiscount\" value=true}\n    {else}\n    {assign var=\"showDiscount\" value=false}\n    {/if}\n    {if count($data.taxes)}\n    {assign var=\"showVat\" value=true}\n    {else}\n    {assign var=\"showVat\" value=false}\n    {/if}\n\n    <div class=\"table-item\">\n        <table>\n            <thead>\n            <tr>\n                <td>{txt key=TEXT_XT_ORDERS_INVOICES_PRODUCTS_MODEL}</td>\n                <td>{txt key=TEXT_XT_ORDERS_INVOICES_PRODUCTS_NAME}</td>\n                <td>{txt key=TEXT_XT_ORDERS_INVOICES_PRODUCTS_QUANTITY}</td>\n                <td>{txt key=TEXT_XT_ORDERS_INVOICES_PRODUCTS_PRICE}</td>\n\n                {if $showDiscount}\n                <td>{txt key=TEXT_XT_ORDERS_INVOICES_PRODUCTS_DISCOUNT}</td>\n                {/if}\n                {if $showVat}\n                <td>{txt key=TEXT_XT_ORDERS_INVOICES_VAT}</td>\n                {/if}\n                <td class=\"total\">{txt key=TEXT_XT_ORDERS_INVOICES_PRODUCTS_TOTAL_PRICE}</td>\n            </tr>\n            </thead>\n            <tbody>\n            {foreach from=$data.products item=product name=products}\n            <tr class=\"{if $smarty.foreach.products.last}last {/if} item\">\n                <td>{$product.products_model}</td>\n                <td class=\"name\">{$product.products_name}\n                    {if $product.serial_number}<br />{txt key=TEXT_SERIAL_NUMBER} {$product.serial_number}{/if}\n                </td>\n\n                <td>{$product.products_quantity}</td>\n                <td>{$product.products_price_formatted}&nbsp;{$data.invoice.invoice_currency}</td>\n\n                {if $showDiscount}\n                <td>{$product.products_total_discount_formatted}&nbsp;{$data.invoice.invoice_currency}</td>\n                {/if}\n                {if $showVat}\n                <td>{$product.products_tax_rate_formatted}%</td>\n                {/if}\n                <td class=\"total\">{$product.products_final_total_price_formatted}&nbsp;{$data.invoice.invoice_currency}</td>\n            </tr>\n            {/foreach}\n\n        </table>\n    </div>\n    <div class=\"clear\"></div>\n    <div class=\"total_table data right alignr\">\n            <ul>\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_SUBTOTAL}: {$data.invoice.invoice_subtotal_price_formatted}&nbsp;{$data.invoice.invoice_currency}</li>\n                {if $data.invoice.invoice_payment_cost > 0}<li>{txt key=TEXT_XT_ORDERS_INVOICES_PAYMENT_COST}&nbsp;({$data.invoice.invoice_payment_name}): {$data.invoice.invoice_payment_cost_formatted}&nbsp;{$data.invoice.invoice_currency}</li>{/if}\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_SHIPPING_COST}: {$data.invoice.invoice_shipping_total_formatted}&nbsp;{$data.invoice.invoice_currency}</li>\n                {foreach from=$data.invoice.totalLines item=\"totalLine\" name=\"totalLines\"}\n                <li>{$totalLine.invoice_payment_name} {$totalLine.invoice_payment_tax_rate}% :  {$totalLine.invoice_payment_cost_formatted}&nbsp;{$data.invoice.invoice_currency}</li>\n                {/foreach}\n                {if count($data.taxes)}\n                {foreach from=$data.taxes item=\"tax\" name=\"taxes\"}\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_VAT} {$tax.rate_formatted}% :  {$tax.amount_formatted}&nbsp;{$data.invoice.invoice_currency}</li>\n                {/foreach}\n                {/if}\n                {if $data.invoice.invoice_coupon_redeem}<li>{txt key=TEXT_XT_ORDERS_INVOICES_COUPON}:  {$data.invoice.invoice_coupon_price_formatted}&nbsp;{$data.invoice.invoice_currency}</li>\n                {/if}\n                <li><strong>{txt key=TEXT_XT_ORDERS_INVOICES_TOTAL}:  {$data.invoice.invoice_total_formatted}&nbsp;{$data.invoice.invoice_currency}</strong></li>\n                <li>{txt key=TEXT_XT_ORDERS_INVOICES_TOTAL_NET}:  {$data.invoice.invoice_total_formatted_net}&nbsp;{$data.invoice.invoice_currency}</li>\n                {hook key=invoice_after_total}\n            <li>{txt key=TEXT_TOTAL_WEIGHT_NET_INVOICE}:  {$data.order.total_weight.formated}</li>\n                {hook key=invoice_totals_bottom}\n            </ul>\n        </div>\n    <div class=\"clear\"></div>\n    <!------------------------------------------------- END ITEM LIST ------------------------------------------------->\n\n    <!------------------------------------------------- START PAYMENT INFO ----------------------------------------------- -->\n    {if $data.order.order_data.payment_info_html}\n    <!--div class=\"payment_info_html data right alignr\">\n        <h2 style=\"font-size:12px;\">{txt key=\"TEXT_PAYMENT_INFOS\"}</h2>\n        {$data.order.order_data.payment_info_html}\n    </div-->\n    {/if}\n    <!------------------------------------------------- END PAYMENT INFO ----------------------------------------------- -->\n\n    <!------------------------------------------------- START BANK DETAILS ------------------------------------------------->\n    <!------------------------------------------------- START BANK DETAILS INVOICE ------------------------------------------------->\n    {if $data.invoice.invoice_bank_details}\n    <div class=\"bankdetails data right alignr\">\n        <ul>\n         